import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import Modal from "../components/Modal";
import authService from "../services/authService";
import "../assets/Home.css"; // Ou crie um específico como ConfigAll.css

const ConfigAll = () => {
  const [usuario, setUsuario] = useState(null);
  const [modalAberto, setModalAberto] = useState(false);
  const [tipoEdicao, setTipoEdicao] = useState("");

  useEffect(() => {
    const usuarioStorage = JSON.parse(localStorage.getItem("usuario"));
    if (usuarioStorage?.id) {
      authService
        .getUserById(usuarioStorage.id)
        .then((res) => setUsuario(res.data))
        .catch((err) =>
          console.error("Erro ao buscar dados do usuário:", err)
        );
    }
  }, []);

  const abrirModal = (tipo) => {
    setTipoEdicao(tipo);
    setModalAberto(true);
  };

  const fecharModal = () => {
    setModalAberto(false);
    setTipoEdicao("");
  };

  if (!usuario) {
    return (
      <Layout>
        <div className="home-container">Carregando informações...</div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="home-container">
        <h1 className="home-header">Configurações da Conta</h1>

        <div className="top-cards">
          <div className="stat-card">
            <strong>E-mail:</strong><br />
            {usuario.email}<br />
            <button onClick={() => abrirModal("email")}>Editar</button>
          </div>

          <div className="stat-card">
            <strong>Senha:</strong><br />
            ********<br />
            <button onClick={() => abrirModal("senha")}>Alterar</button>
          </div>

          <div className="stat-card">
            <strong>Rendimento:</strong><br />
            {usuario.renda?.toLocaleString("pt-BR", {
              style: "currency",
              currency: "BRL",
            })}<br />
            <button onClick={() => abrirModal("rendimento")}>Editar</button>
          </div>
        </div>

        {modalAberto && (
          <Modal
            tipo={tipoEdicao}
            onClose={fecharModal}
            usuario={usuario}
            atualizarUsuario={setUsuario}
          />
        )}
      </div>
    </Layout>
  );
};

export default ConfigAll;
