import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import authService from "../services/authService";
import EditEmailPopup from "../components/EditEmailPopup";
import EditSenhaPopup from "../components/EditSenhaPopup";
import EditRendaPopup from "../components/EditRendaPopup";
import "../assets/Config.css";

const ConfigAll = () => {
  const [usuario, setUsuario] = useState(null);
  const [showEmailPopup, setShowEmailPopup] = useState(false);
  const [showSenhaPopup, setShowSenhaPopup] = useState(false);
  const [showRendaPopup, setShowRendaPopup] = useState(false);

  useEffect(() => {
    const localUser = JSON.parse(localStorage.getItem("usuario"));
    if (localUser?.id) {
      authService.getUserById(localUser.id)
        .then(res => setUsuario(res.data))
        .catch(err => console.error("Erro ao buscar usuário:", err));
    }
  }, []);

  const atualizarUsuario = () => {
    const localUser = JSON.parse(localStorage.getItem("usuario"));
    if (localUser?.id) {
      authService.getUserById(localUser.id)
        .then(res => setUsuario(res.data))
        .catch(err => console.error("Erro ao atualizar dados:", err));
    }
  };

  return (
    <Layout>
      <div className="config-all-container">
        <h1 className="config-header">Configurações da Conta</h1>

        {usuario && (
          <div className="user-info-box">
            <p><strong>Nome:</strong> {usuario.nome}</p>
            <p><strong>E-mail:</strong> {usuario.email}</p>
            <p><strong>Rendimento:</strong> {usuario.renda.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })}</p>

            <div className="button-group">
              <button onClick={() => setShowEmailPopup(true)}>Alterar E-mail</button>
              <button onClick={() => setShowSenhaPopup(true)}>Alterar Senha</button>
              <button onClick={() => setShowRendaPopup(true)}>Alterar Rendimento</button>
            </div>
          </div>
        )}

        {/* Popups */}
        {showEmailPopup && (
          <EditEmailPopup
            usuario={usuario}
            onClose={() => setShowEmailPopup(false)}
            onUpdate={atualizarUsuario}
          />
        )}

        {showSenhaPopup && (
          <EditSenhaPopup
            usuario={usuario}
            onClose={() => setShowSenhaPopup(false)}
            onUpdate={atualizarUsuario}
          />
        )}

        {showRendaPopup && (
          <EditRendaPopup
            usuario={usuario}
            onClose={() => setShowRendaPopup(false)}
            onUpdate={atualizarUsuario}
          />
        )}
      </div>
    </Layout>
  );
};

export default ConfigAll;
