import React, { useState } from "react"; // Importa React e o hook useState para gerenciar estados locais
import { login } from "../services/authService"; // Importa a função de login do serviço de autenticação

const Login = () => {
  // Estados para armazenar email, senha e possíveis mensagens de erro
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // Função chamada ao enviar o formulário de login
  const handleLogin = async (e) => {
    e.preventDefault(); // Evita que a página seja recarregada ao enviar o formulário
    const success = await login(email, password); // Chama a função de login passando email e senha
    if (success) {
      alert("Login bem-sucedido!"); // Exibe alerta caso o login seja bem-sucedido
    } else {
      setError("Credenciais inválidas."); // Define uma mensagem de erro se falhar
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {error && <p style={{ color: "red" }}>{error}</p>} {/* Exibe mensagem de erro caso exista */}
      <form onSubmit={handleLogin}>
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input type="password" placeholder="Senha" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <button type="submit">Entrar</button>
      </form>
    </div>
  );
};

export default Login;
