import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../services/authService";
import Header from "../components/Header";

export default function Config() {
  const [usuario, setUsuario] = useState({});
  const [email, setEmail] = useState("");
  const [novaSenha, setNovaSenha] = useState("");
  const [senhaAtual, setSenhaAtual] = useState("");
  const [emailConfirmacao, setEmailConfirmacao] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const userLogado = authService.getUsuario();
    if (userLogado) {
      setUsuario(userLogado);
      setEmail(userLogado.email);
    } else {
      navigate("/login");
    }
  }, []);

  const handleSalvar = async () => {
    try {
      const dados = {
        nome: usuario.nome,
        email: email
      };

      if (email !== usuario.email) {
        await authService.alterarEmail(usuario.id, email);
        alert("Email alterado com sucesso!");
      }

      if (novaSenha && senhaAtual && email === emailConfirmacao) {
        await authService.alterarSenha(usuario.id, senhaAtual, novaSenha);
        alert("Senha atualizada com sucesso!");
      } else if (novaSenha || senhaAtual) {
        alert("Certifique-se de preencher todos os campos de senha e confirmar o email corretamente.");
        return;
      }

      const userAtualizado = authService.getUsuario();
      userAtualizado.email = email;
      authService.salvarUsuario(userAtualizado);
    } catch (error) {
      console.error(error);
      alert("Erro ao atualizar dados.");
    }
  };

  return (
    <div>
      <Header />
      <div className="container">
        <h2>Configurações da Conta</h2>
        <div className="form-group">
          <label>Email atual:</label>
          <input type="email" value={usuario.email} disabled />
        </div>
        <div className="form-group">
          <label>Novo email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div className="form-group">
          <label>Confirmar novo email:</label>
          <input type="email" value={emailConfirmacao} onChange={(e) => setEmailConfirmacao(e.target.value)} />
        </div>
        <div className="form-group">
          <label>Senha atual:</label>
          <input type="password" value={senhaAtual} onChange={(e) => setSenhaAtual(e.target.value)} />
        </div>
        <div className="form-group">
          <label>Nova senha:</label>
          <input type="password" value={novaSenha} onChange={(e) => setNovaSenha(e.target.value)} />
        </div>
        <button className="btn btn-primary" onClick={handleSalvar}>Salvar alterações</button>
      </div>
    </div>
  );
}
