import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../assets/Login.css"; // Estilos visuais da página
import { login } from "../services/authService"; // Função de login no back-end

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mensagemErro, setMensagemErro] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const data = await login(email, password); // Chamada ao serviço de login

      console.log("Login realizado com sucesso:", data);

      // Aqui você pode salvar o token ou o nome do usuário (se o back-end retornar)
      // Exemplo: localStorage.setItem("token", data.token);

      // Redireciona para a página Home
      navigate("/home");
    } catch (error) {
      console.error("Erro ao fazer login:", error);
      setMensagemErro("E-mail ou senha inválidos.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-image-container"></div> {/* Imagem do rosto anônimo */}
      <h2 className="login-title">Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <label htmlFor="email">E-mail</label>
          <input
            type="email"
            id="email"
            className="input-field"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="password">Senha</label>
          <input
            type="password"
            id="password"
            className="input-field"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        {/* Exibe mensagem de erro se houver */}
        {mensagemErro && <p style={{ color: "red" }}>{mensagemErro}</p>}

        <button type="submit" className="login-button">Entrar</button>
      </form>
      <p className="signup-link">
        Não tem uma conta? <Link to="/registro">Criar uma conta</Link>
      </p>
    </div>
  );
};

export default Login;
