import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import Modal from "../components/Modal";
import authService from "../services/authService";
import "../assets/ConfigAll.css"; // Keep using the same CSS file, but its content will change

const ConfigAll = () => {
  const [usuario, setUsuario] = useState(null);
  const [modalAberto, setModalAberto] = useState(false);
  const [tipoEdicao, setTipoEdicao] = useState("");

  // We'll manage the displayed values directly from the `usuario` state for simplicity
  // The modal will handle the actual input and update logic.

  useEffect(() => {
    const usuarioStorage = JSON.parse(localStorage.getItem("usuario"));
    if (usuarioStorage?.id) {
      authService
        .getUserById(usuarioStorage.id)
        .then((res) => setUsuario(res.data))
        .catch((err) =>
          console.error("Erro ao buscar dados do usuário:", err)
        );
    }
  }, []);

  const abrirModal = (tipo) => {
    setTipoEdicao(tipo);
    setModalAberto(true);
  };

  const fecharModal = () => {
    setModalAberto(false);
    setTipoEdicao("");
    // Optionally re-fetch user data or update local state after modal closes
    // if the modal handles the update directly and you want to ensure fresh data
    const usuarioStorage = JSON.parse(localStorage.getItem("usuario"));
    if (usuarioStorage?.id) {
      authService
        .getUserById(usuarioStorage.id)
        .then((res) => setUsuario(res.data))
        .catch((err) =>
          console.error("Erro ao buscar dados do usuário:", err)
        );
    }
  };

  if (!usuario) {
    return (
      <Layout>
        <div className="profile-container">Carregando informações...</div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="profile-container">
        <div className="profile-card-simplified">
          <h1 className="profile-header-simplified">Meu Perfil</h1>

          {/* E-mail Field */}
          <div className="info-item">
            <span className="info-label">E-mail:</span>
            <span className="info-value">{usuario.email}</span>
            <button
              className="edit-button-simplified"
              onClick={() => abrirModal("email")}
            >
              Editar
            </button>
          </div>

          {/* Nome Field (not editable via button) */}
          <div className="info-item">
            <span className="info-label">Nome:</span>
            <span className="info-value">{usuario.nome || "Não informado"}</span>
            {/* No edit button for name */}
          </div>

          {/* Senha Field */}
          <div className="info-item">
            <span className="info-label">Senha:</span>
            <span className="info-value">********</span>
            <button
              className="edit-button-simplified"
              onClick={() => abrirModal("senha")}
            >
              Alterar
            </button>
          </div>

          {/* Rendimento Field */}
          <div className="info-item">
            <span className="info-label">Rendimento:</span>
            <span className="info-value">
              {usuario.renda?.toLocaleString("pt-BR", {
                style: "currency",
                currency: "BRL",
              }) || "Não informado"}
            </span>
            <button
              className="edit-button-simplified"
              onClick={() => abrirModal("rendimento")}
            >
              Editar
            </button>
          </div>
        </div>

        {modalAberto && (
          <Modal
            tipo={tipoEdicao}
            onClose={fecharModal}
            usuario={usuario}
            atualizarUsuario={setUsuario} // Pass setUsuario to update the parent state
          />
        )}
      </div>
    </Layout>
  );
};

export default ConfigAll;
