// src/pages/Lancamentos.js
import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import "../assets/Lancamentos.css";
import axios from "axios";

const Lancamentos = () => {
  const [lancamentos, setLancamentos] = useState([]);
  const [paginaAtual, setPaginaAtual] = useState(1);
  const porPagina = 15;

  useEffect(() => {
    const usuario = JSON.parse(localStorage.getItem("usuario"));
    if (usuario?.id) {
      axios
        .get(`http://localhost:9087/gasto/usuario/all?idUsuario=${usuario.id}`)
        .then((res) => setLancamentos(res.data))
        .catch((err) => console.error("Erro ao carregar lançamentos:", err));
    }
  }, []);

  // Paginação
  const totalPaginas = Math.ceil(lancamentos.length / porPagina);
  const lancamentosVisiveis = lancamentos.slice(
    (paginaAtual - 1) * porPagina,
    paginaAtual * porPagina
  );

  const mudarPagina = (novaPagina) => {
    if (novaPagina >= 1 && novaPagina <= totalPaginas) {
      setPaginaAtual(novaPagina);
    }
  };

  return (
    <Layout>
      <div className="lancamentos-container">
        <div className="header-bar">
          <h2>Meus Lançamentos</h2>
          <button className="novo-button">+ Novo Lançamento</button>
        </div>

        <input
          type="text"
          className="search-bar"
          placeholder="Pesquisar lançamentos..."
        />

        <table className="lancamentos-table">
          <thead>
            <tr>
              <th>Descrição</th>
              <th>Tipo</th>
              <th>Valor</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {lancamentosVisiveis.map((item) => (
              <tr key={item.id}>
                <td>{item.descricao}</td>
                <td>{item.tipo}</td>
                <td>R$ {item.valor.toFixed(2)}</td>
                <td className="acoes">
                  <button className="editar">Editar</button>
                  <button className="excluir">Excluir</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Paginação */}
        <div className="paginacao">
          <button onClick={() => mudarPagina(paginaAtual - 1)} disabled={paginaAtual === 1}>
            Anterior
          </button>
          <span>Página {paginaAtual} de {totalPaginas}</span>
          <button onClick={() => mudarPagina(paginaAtual + 1)} disabled={paginaAtual === totalPaginas}>
            Próxima
          </button>
        </div>
      </div>
    </Layout>
  );
};

export default Lancamentos;
