// src/components/EditRendaPopup.js
import React, { useState } from "react";
import authService from "../services/authService";
import "../assets/Popup.css";

const EditRendaPopup = ({ usuario, onClose }) => {
  const [novaRenda, setNovaRenda] = useState(usuario.renda || 0);
  const [mensagem, setMensagem] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    // Ensure 'name' is always a non-null string.
    // If usuario.nome is null/undefined, it defaults to an empty string.
    const nomeDoUsuario = usuario.nome ? String(usuario.nome) : "";

    // If the name is truly empty/null from the system,
    // we should display an error and prevent submission.
    // This avoids sending an invalid request to the backend.
    if (!nomeDoUsuario) {
      setMensagem("Erro: Não foi possível obter o nome do usuário. Por favor, recarregue a página.");
      return; // Prevent submission
    }

    const dadosParaAtualizar = {
      name: nomeDoUsuario,
      renda: novaRenda,
    };

    console.log("Dados a serem enviados para alterarDadosUsuario:", dadosParaAtualizar); // For debugging
    console.log("Usuario ID para alterarDadosUsuario:", usuario.id); // For debugging

    authService.alterarDadosUsuario(usuario.id, dadosParaAtualizar)
      .then(() => {
        setMensagem("Rendimento atualizado com sucesso!");
        setTimeout(() => {
          setMensagem("");
          onClose(); // Close the popup and trigger re-fetch in ConfigAll
        }, 1500);
      })
      .catch((error) => {
        console.error("Erro ao atualizar rendimento:", error.response?.data || error.message);
        const backendMessage = error.response?.data?.message;
        // Check if the backend message is specifically about the name
        if (backendMessage && backendMessage.includes("Nome") && (backendMessage.includes("vazio") || backendMessage.includes("nulo"))) {
            setMensagem("Erro: O nome fornecido não é válido. Verifique seus dados de perfil.");
        } else {
            setMensagem(backendMessage || "Erro ao atualizar rendimento. Tente novamente.");
        }
      });
  };

  return (
    <div className="popup">
      <h3>Editar Rendimento</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          step="0.01"
          value={novaRenda}
          onChange={(e) => setNovaRenda(parseFloat(e.target.value))}
          required
        />
        <button type="submit">Salvar</button>
        <button type="button" onClick={onClose}>Cancelar</button>
        {mensagem && <p className="popup-message">{mensagem}</p>}
      </form>
    </div>
  );
};

export default EditRendaPopup;
