import React, { useState } from "react";
import authService from "../services/authService";
import "../assets/Popup.css";

const EditSenhaPopup = ({ usuario, onClose }) => {
  const [senhaAtual, setSenhaAtual] = useState("");
  const [novaSenha, setNovaSenha] = useState("");
  const [mensagem, setMensagem] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const requestBody = { // Renamed to requestBody to clarify it's the body of the request
      email: usuario.email, // Include email as per your API documentation image
      senhaAtual,
      novaSenha,
    };

    authService.alterarSenha(usuario.id, requestBody) // Pass id and the requestBody object
      .then(() => {
        setMensagem("Senha atualizada com sucesso!");
        setTimeout(onClose, 1500);
      })
      .catch((error) => {
        console.error("Erro ao atualizar senha:", error.response?.data || error.message);
        setMensagem("Erro ao atualizar senha. Verifique sua senha atual.");
      });
  };

  return (
    <div className="popup">
      <h3>Alterar Senha</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="password"
          placeholder="Senha atual"
          value={senhaAtual}
          onChange={(e) => setSenhaAtual(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Nova senha"
          value={novaSenha}
          onChange={(e) => setNovaSenha(e.target.value)}
          required
        />
        <button type="submit">Salvar</button>
        <button type="button" onClick={onClose}>Cancelar</button>
        {mensagem && <p className="popup-message">{mensagem}</p>}
      </form>
    </div>
  );
};

export default EditSenhaPopup;
