// src/components/NovoModal.js
import React, { useEffect, useState } from "react";
import "../assets/EditarModal.css";

const categorias = {
  NECESSIDADES: [
    "aluguel", "condominio", "conta de agua", "conta de luz", "gás", "plano de saude", "remedios",
    "transporte", "combustivel", "parcela de carro", "seguro de carro", "seguro de vida",
    "supermercado", "faculdade", "internet"
  ],
  DESEJOS: [
    "assinaturas", "cinema", "delivery", "eletronicos", "massagem", "presentes",
    "restaurante", "roupa", "salão de beleza", "shows", "streaming", "cursos livres",
    "transporte por aplicativo", "servicos", "academia", "hobby"
  ],
  INVESTIMENTO_E_POUPANCA: [
    "fundo de emergência", "investimento", "poupança", "previdência"
  ]
};

const categoriaFormatada = {
  NECESSIDADES: "Necessidade",
  DESEJOS: "Desejo",
  INVESTIMENTO_E_POUPANCA: "Investimento/Poupança"
};

const NovoModal = ({ onClose, onSalvarNovo }) => {
  const [valor, setValor] = useState("");
  const [categoria, setCategoria] = useState("NECESSIDADES");
  const [subcategoria, setSubcategoria] = useState(categorias.NECESSIDADES[0]);
  // Add a state for date, initialized to today's date in YYYY-MM-DD for input type="date"
  const [dataLancamento, setDataLancamento] = useState(new Date().toISOString().split('T')[0]); // YYYY-MM-DD
  // Add a state for time, initialized to current time
  const [horaLancamento, setHoraLancamento] = useState(new Date().toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit', second: '2-digit', hour12: false }));

  useEffect(() => {
    setSubcategoria(categorias[categoria][0]);
  }, [categoria]);

  const handleSalvar = () => {
    const usuario = JSON.parse(localStorage.getItem("usuario"));
    const idUsuario = usuario?.id || usuario?.idUsuario;

    if (!idUsuario || !valor || !categoria || !subcategoria || !dataLancamento || !horaLancamento) {
      alert("Por favor, preencha todos os campos.");
      return;
    }

    // Combine date (YYYY-MM-DD) and time (HH:MM:SS) to create an ISO 8601 string
    // This will create a local date/time object, then convert to ISO string (UTC)
    const combinedDateTimeString = `${dataLancamento}T${horaLancamento}`; // e.g., "2025-05-29T23:30:00"
    const dataHoraISO = new Date(combinedDateTimeString).toISOString();

    const novoGasto = {
      valor: parseFloat(valor),
      categoria,
      subcategoria,
      dataHora: dataHoraISO // Now sends ISO 8601 to the backend
    };

    onSalvarNovo(idUsuario, novoGasto);
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h3>Novo Lançamento</h3>

        <label>Valor:</label>
        <input
          type="number"
          value={valor}
          onChange={(e) => setValor(e.target.value)}
        />

        <label>Categoria:</label>
        <select value={categoria} onChange={(e) => setCategoria(e.target.value)}>
          {Object.keys(categorias).map((cat) => (
            <option key={cat} value={cat}>
              {categoriaFormatada[cat]}
            </option>
          ))}
        </select>

        <label>Subcategoria:</label>
        <select value={subcategoria} onChange={(e) => setSubcategoria(e.target.value)}>
          {categorias[categoria].map((sub) => (
            <option key={sub} value={sub}>{sub}</option>
          ))}
        </select>

        <label>Data:</label>
        <input
          type="date"
          value={dataLancamento}
          onChange={(e) => setDataLancamento(e.target.value)}
        />

        <label>Hora:</label>
        <input
          type="time"
          value={horaLancamento}
          onChange={(e) => setHoraLancamento(e.target.value)}
          step="1" // Allows seconds input
        />


        <div className="modal-buttons">
          <button className="salvar" onClick={handleSalvar}>Salvar</button>
          <button className="cancelar" onClick={onClose}>Cancelar</button>
        </div>
      </div>
    </div>
  );
};

export default NovoModal;
