// src/components/EditRendaPopup.js
import React, { useState, useEffect } from "react";
import authService from "../services/authService";
import "../assets/Popup.css";

const EditRendaPopup = ({ usuario, onClose }) => {
  const [novaRenda, setNovaRenda] = useState(usuario.renda || 0);
  const [mensagem, setMensagem] = useState("");

  useEffect(() => {
    // Log the user prop when the component mounts or updates, for debugging
    console.log("EditRendaPopup - Prop 'usuario' recebida:", usuario);
    if (usuario && !usuario.nome) {
      console.warn("EditRendaPopup - 'usuario.nome' está ausente ou é nulo/vazio na prop:", usuario.nome);
      // Optional: Set a message here if you want to alert the user immediately
      // setMensagem("Atenção: O nome do usuário não foi carregado corretamente. A atualização pode falhar.");
    }
  }, [usuario]); // Dependency array: run when 'usuario' prop changes

  const handleSubmit = (e) => {
    e.preventDefault();

    // Ensure 'nomeDoUsuario' is always a non-null, non-empty string.
    // Use String() constructor to forcefully convert to string, then trim.
    const nomeDoUsuario = usuario?.nome ? String(usuario.nome).trim() : "";

    // IMPORTANT: If your backend strictly requires a non-empty name
    // and 'usuario.nome' from your system is somehow empty/null/undefined,
    // this check will prevent sending a bad request.
    if (!nomeDoUsuario) {
      setMensagem("Erro: O nome do usuário não está disponível no sistema. Não é possível atualizar o rendimento.");
      console.error("Tentativa de enviar renda com nome vazio. Objeto usuario:", usuario);
      return; // Prevent submission
    }

    const dadosParaAtualizar = {
      name: nomeDoUsuario, // Ensure the key is 'name' as per Swagger
      renda: novaRenda,
    };

    console.log("EditRendaPopup - Dados a serem enviados (payload):", dadosParaAtualizar);
    console.log("EditRendaPopup - Usuario ID para requisição:", usuario.id);

    authService.alterarDadosUsuario(usuario.id, dadosParaAtualizar)
      .then(() => {
        setMensagem("Rendimento atualizado com sucesso!");
        setTimeout(() => {
          setMensagem(""); // Clear message before closing
          onClose(); // Close the popup
        }, 1500);
      })
      .catch((error) => {
        console.error("Erro ao atualizar rendimento (detalhes completos):", error.response || error.message);

        // Extract messages from backend response
        let errorMessage = "Erro ao atualizar rendimento. Tente novamente.";
        if (error.response && error.response.data) {
          const backendData = error.response.data;
          if (backendData.message) {
            if (Array.isArray(backendData.message)) {
              // Join multiple messages from the backend array
              errorMessage = backendData.message.join(", ");
            } else if (typeof backendData.message === 'string') {
              errorMessage = backendData.message;
            }
          } else if (backendData.error) {
              errorMessage = backendData.error;
          }
        }

        // Customize the message if it's still about the name field
        if (errorMessage.includes("Nome") &&
            (errorMessage.includes("vazio") || errorMessage.includes("nulo") || errorMessage.includes("preenchido incorretamente"))) {
            setMensagem("Erro: O nome no seu perfil não foi aceito pelo sistema. Verifique o nome ou contate o suporte."); // Your specific message
        } else {
            setMensagem(errorMessage); // Fallback to backend message
        }
      });
  };

  return (
    <div className="popup">
      <h3>Editar Rendimento</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          step="0.01"
          value={novaRenda}
          onChange={(e) => setNovaRenda(parseFloat(e.target.value))}
          required
        />
        <button type="submit">Salvar</button>
        <button type="button" onClick={onClose}>Cancelar</button>
        {mensagem && <p className="popup-message">{mensagem}</p>}
      </form>
    </div>
  );
};

export default EditRendaPopup;
