// src/pages/AlterarEmail.js
import React, { useState } from "react";
import Layout from "../components/Layout";
import authService from "../services/authService";
import "./styles/Config.css";

const AlterarEmail = () => {
  const [email, setEmail] = useState("");
  const [mensagem, setMensagem] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const usuarioId = localStorage.getItem("usuarioId");
    try {
      await authService.updateUser({ id: usuarioId, email });
      setMensagem("Email alterado com sucesso!");
    } catch (error) {
      console.error("Erro ao alterar o email:", error);
      setMensagem("Erro ao alterar o email.");
    }
  };

  return (
    <Layout>
      <div className="config-container">
        <h2>Alterar Email</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="email">Novo Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            required
            onChange={(e) => setEmail(e.target.value)}
          />
          <button type="submit">Salvar</button>
        </form>
        {mensagem && <p className="mensagem">{mensagem}</p>}
      </div>
    </Layout>
  );
};

export default AlterarEmail;
