import React, { useState } from "react";
import authService from "../services/authService";
import "../assets/Popup.css";

const EditRendaPopup = ({ usuario, onClose }) => {
  const [novaRenda, setNovaRenda] = useState(usuario.renda || 0);
  const [mensagem, setMensagem] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    // Ensure usuario.nome is a string, even if null/undefined
    const nomeDoUsuario = usuario.nome ? String(usuario.nome) : "";

    const dadosParaAtualizar = {
      name: nomeDoUsuario, // Ensure 'name' is always a string
      renda: novaRenda,
    };

    console.log("Dados a serem enviados para alterarDadosUsuario:", dadosParaAtualizar); // For debugging
    console.log("Usuario ID para alterarDadosUsuario:", usuario.id); // For debugging

    authService.alterarDadosUsuario(usuario.id, dadosParaAtualizar)
      .then(() => {
        setMensagem("Rendimento atualizado com sucesso!");
        setTimeout(() => {
          setMensagem("");
          onClose(); // Close the popup and trigger re-fetch in ConfigAll
        }, 1500);
      })
      .catch((error) => {
        console.error("Erro ao atualizar rendimento:", error.response?.data || error.message);
        // Display a more specific error message if available from backend
        const errorMessage = error.response?.data?.message || "Erro ao atualizar rendimento. Verifique os dados e tente novamente.";
        setMensagem(errorMessage);
      });
  };

  return (
    <div className="popup">
      <h3>Editar Rendimento</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          step="0.01"
          value={novaRenda}
          onChange={(e) => setNovaRenda(parseFloat(e.target.value))}
          required
        />
        <button type="submit">Salvar</button>
        <button type="button" onClick={onClose}>Cancelar</button>
        {mensagem && <p className="popup-message">{mensagem}</p>}
      </form>
    </div>
  );
};

export default EditRendaPopup;
