import React, { useState } from "react";
import authService from "../services/authService";
import "../assets/Popup.css";

const EditSenhaPopup = ({ usuario, onClose }) => {
  const [email, setEmail] = useState(usuario.email); // Pre-fill email from user prop
  const [senhaAntiga, setSenhaAntiga] = useState(""); // Matches API: senhaAntiga
  const [novaSenha, setNovaSenha] = useState("");     // Matches API: senhaNova
  const [confirmarNovaSenha, setConfirmarNovaSenha] = useState(""); // New field for confirmation
  const [mensagem, setMensagem] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (novaSenha !== confirmarNovaSenha) {
      setMensagem("As novas senhas não coincidem!");
      return; // Stop the submission
    }

    const requestBody = {
      email: email, // Use the pre-filled email
      senhaAntiga: senhaAntiga, // Use senhaAntiga from state
      senhaNova: novaSenha,     // Use novaSenha from state
    };

    authService.alterarSenha(usuario.id, requestBody)
      .then(() => {
        setMensagem("Senha atualizada com sucesso!");
        // Clear password fields on success for security
        setSenhaAntiga("");
        setNovaSenha("");
        setConfirmarNovaSenha("");
        setTimeout(onClose, 1500);
      })
      .catch((error) => {
        console.error("Erro ao atualizar senha:", error.response?.data || error.message);
        setMensagem("Erro ao atualizar senha. Verifique a senha antiga e tente novamente.");
      });
  };

  return (
    <div className="popup">
      <h3>Alterar Senha</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          value={email}
          readOnly // Make email field read-only
          disabled // Visually indicate it's not editable
          className="read-only-input" // Add a class for specific styling if needed
        />
        <input
          type="password"
          placeholder="Senha Antiga" // Changed placeholder
          value={senhaAntiga}
          onChange={(e) => setSenhaAntiga(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Nova Senha" // Changed placeholder
          value={novaSenha}
          onChange={(e) => setNovaSenha(e.target.value)}
          required
        />
        <input // New field for confirming new password
          type="password"
          placeholder="Confirmar Nova Senha"
          value={confirmarNovaSenha}
          onChange={(e) => setConfirmarNovaSenha(e.target.value)}
          required
        />
        <button type="submit">Salvar</button>
        <button type="button" onClick={onClose}>Cancelar</button>
        {mensagem && <p className="popup-message">{mensagem}</p>}
      </form>
    </div>
  );
};

export default EditSenhaPopup;
