// src/pages/Lancamentos.js
import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import authService from "../services/authService";
import EditarModal from "../components/EditarModal";
import NovoModal from "../components/NovoModal";
import "../assets/Lancamentos.css";

const categoriasMap = {
  NECESSIDADES: "Necessidade",
  DESEJOS: "Desejo",
  INVESTIMENTO_E_POUPANCA: "Investimento/Poupança"
};

const subcategoriasPorCategoria = {
  NECESSIDADES: [
    "aluguel", "condominio", "conta de agua", "conta de luz", "gás", "plano de saude",
    "remedios", "transporte", "combustivel", "parcela de carro", "seguro de carro",
    "seguro de vida", "supermercado", "faculdade", "internet"
  ],
  DESEJOS: [
    "assinaturas", "cinema", "delivery", "eletronicos", "massagem", "presentes",
    "restaurante", "roupa", "salão de beleza", "shows", "streaming", "cursos livres",
    "transporte por aplicativo", "servicos", "academia", "hobby"
  ],
  INVESTIMENTO_E_POUPANCA: [
    "fundo de emergência", "investimento", "poupança", "previdência"
  ]
};

const Lancamentos = () => {
  const [lancamentos, setLancamentos] = useState([]);
  const [paginaAtual, setPaginaAtual] = useState(1);
  const [modalAberto, setModalAberto] = useState(false);
  const [lancamentoSelecionado, setLancamentoSelecionado] = useState(null);
  const [novoModalAberto, setNovoModalAberto] = useState(false);
  const [categoriaFiltro, setCategoriaFiltro] = useState("");
  const [subcategoriaFiltro, setSubcategoriaFiltro] = useState("");
  const [dataFiltro, setDataFiltro] = useState("");

  const itensPorPagina = 15;

  useEffect(() => {
    buscarLancamentos();
  }, []);

  const buscarLancamentos = () => {
    const usuario = JSON.parse(localStorage.getItem("usuario"));
    if (usuario?.id) {
      authService.buscarLancamentosPorUsuario(usuario.id)
        .then(res => setLancamentos(res.data))
        .catch(err => console.error("Erro ao buscar lançamentos", err));
    }
  };

  const lancamentosFiltrados = lancamentos.filter((l) => {
    const dataFormatada = new Date(l.dataHora).toISOString().split("T")[0];
    return (
      (!categoriaFiltro || l.categoria === categoriaFiltro) &&
      (!subcategoriaFiltro || l.subcategoria === subcategoriaFiltro) &&
      (!dataFiltro || dataFormatada === dataFiltro)
    );
  });

  const totalPaginas = Math.ceil(lancamentosFiltrados.length / itensPorPagina);
  const lancamentosExibidos = lancamentosFiltrados.slice(
    (paginaAtual - 1) * itensPorPagina,
    paginaAtual * itensPorPagina
  );

  const mudarPagina = (novaPagina) => {
    if (novaPagina >= 1 && novaPagina <= totalPaginas) {
      setPaginaAtual(novaPagina);
    }
  };

  const handleExcluir = async (id) => {
    if (window.confirm("Deseja realmente excluir este lançamento?")) {
      try {
        await authService.deletarGasto(id);
        setLancamentos(lancamentos.filter(l => l.id !== id));
      } catch (error) {
        alert("Erro ao excluir lançamento.");
        console.error("Erro ao excluir gasto:", error);
      }
    }
  };

  const abrirModal = (lancamento) => {
    setLancamentoSelecionado(lancamento);
    setModalAberto(true);
  };

  const salvarAlteracoes = async (lancamentoEditado) => {
    try {
      await authService.alterarGasto(lancamentoEditado.id, {
        valor: lancamentoEditado.valor,
        categoria: lancamentoEditado.categoria,
        subcategoria: lancamentoEditado.subcategoria,
        dataHora: lancamentoEditado.dataHora,
      });

      setLancamentos(prev =>
        prev.map(item => item.id === lancamentoEditado.id ? lancamentoEditado : item)
      );

      setModalAberto(false);
    } catch (error) {
      console.error("Erro ao salvar alteração:", error);
      alert("Erro ao salvar alteração no lançamento.");
    }
  };

  return (
    <Layout>
      <div className="lancamentos-container">
        <div className="header-bar">
          <h2>Lançamentos</h2>
          <button className="novo-button" onClick={() => setNovoModalAberto(true)}>
            + Novo Lançamento
          </button>
        </div>

        {/* Filtros */}
        <div className="filtros-container">
          <select
            value={categoriaFiltro}
            onChange={(e) => {
              setCategoriaFiltro(e.target.value);
              setSubcategoriaFiltro("");
            }}
          >
            <option value="">Todas as Categorias</option>
            {Object.keys(categoriasMap).map(key => (
              <option key={key} value={key}>{categoriasMap[key]}</option>
            ))}
          </select>

          <select
            value={subcategoriaFiltro}
            onChange={(e) => setSubcategoriaFiltro(e.target.value)}
            disabled={!categoriaFiltro}
          >
            <option value="">Todas as Subcategorias</option>
            {categoriaFiltro &&
              subcategoriasPorCategoria[categoriaFiltro]?.map(sub => (
                <option key={sub} value={sub}>{sub}</option>
              ))}
          </select>

          <input
            type="date"
            value={dataFiltro}
            onChange={(e) => setDataFiltro(e.target.value)}
          />
        </div>

        <table className="lancamentos-table">
          <thead>
            <tr>
              <th>Valor</th>
              <th>Categoria</th>
              <th>Subcategoria</th>
              <th>Data</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {lancamentosExibidos.map((item) => (
              <tr key={item.id}>
                <td>{item.valor.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })}</td>
                <td>{categoriasMap[item.categoria] || item.categoria}</td>
                <td>{item.subcategoria}</td>
                <td>{new Date(item.dataHora).toLocaleDateString("pt-BR")}</td>
                <td className="acoes">
                  <button className="editar" onClick={() => abrirModal(item)}>Editar</button>
                  <button className="excluir" onClick={() => handleExcluir(item.id)}>Excluir</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="paginacao">
          <button onClick={() => mudarPagina(paginaAtual - 1)} disabled={paginaAtual === 1}>Anterior</button>
          <span>Página {paginaAtual} de {totalPaginas}</span>
          <button onClick={() => mudarPagina(paginaAtual + 1)} disabled={paginaAtual === totalPaginas}>Próxima</button>
        </div>
      </div>

      {modalAberto && (
        <EditarModal
          lancamento={lancamentoSelecionado}
          onClose={() => setModalAberto(false)}
          onSave={salvarAlteracoes}
        />
      )}

      {novoModalAberto && (
        <NovoModal
          onClose={() => setNovoModalAberto(false)}
          onSalvarNovo={(idUsuario, novoGasto) => {
            authService.cadastrarGasto(idUsuario, novoGasto)
              .then(() => {
                buscarLancamentos();
                setNovoModalAberto(false);
              })
              .catch(err => {
                console.error("Erro ao cadastrar gasto:", err);
                alert("Erro ao cadastrar novo lançamento.");
              });
          }}
        />
      )}
    </Layout>
  );
};

export default Lancamentos;
