// src/pages/Lancamentos.js
import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import authService from "../services/authService";
import "../assets/Lancamentos.css"; // Garanta que esse CSS esteja estilizando corretamente

const Lancamentos = () => {
  const [gastos, setGastos] = useState([]);
  const [paginaAtual, setPaginaAtual] = useState(1);
  const itensPorPagina = 7;

  useEffect(() => {
    const fetchGastos = async () => {
      const usuario = JSON.parse(localStorage.getItem("usuario"));
      if (usuario && usuario.id) {
        try {
          const response = await authService.buscarGastosPorUsuario(usuario.id);
          setGastos(response.data);
        } catch (error) {
          console.error("Erro ao buscar lançamentos:", error);
        }
      }
    };

    fetchGastos();
  }, []);

  const inicio = (paginaAtual - 1) * itensPorPagina;
  const fim = inicio + itensPorPagina;
  const gastosPaginados = gastos.slice(inicio, fim);
  const totalPaginas = Math.ceil(gastos.length / itensPorPagina);

  const irParaPagina = (pagina) => {
    if (pagina >= 1 && pagina <= totalPaginas) {
      setPaginaAtual(pagina);
    }
  };

  const handleExcluir = async (id) => {
    if (window.confirm("Deseja realmente excluir este lançamento?")) {
      try {
        await authService.delet(id);
        setGastos(gastos.filter(gasto => gasto.id !== id));
      } catch (error) {
        alert("Erro ao excluir lançamento.");
        console.error(error);
      }
    }
  };

  return (
    <Layout>
      <div className="lancamentos-container">
        <h2>Lançamentos</h2>
        <table className="lancamentos-table">
          <thead>
            <tr>
              <th>Valor</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {gastosPaginados.map((gasto) => (
              <tr key={gasto.id}>
                <td>R$ {gasto.valor.toFixed(2)}</td>
                <td>
                  <button className="editar-btn">Editar</button>
                  <button className="excluir-btn" onClick={() => handleExcluir(gasto.id)}>Excluir</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="paginacao">
          <button onClick={() => irParaPagina(paginaAtual - 1)} disabled={paginaAtual === 1}>Anterior</button>
          <span>Página {paginaAtual} de {totalPaginas}</span>
          <button onClick={() => irParaPagina(paginaAtual + 1)} disabled={paginaAtual === totalPaginas}>Próxima</button>
        </div>
      </div>
    </Layout>
  );
};

export default Lancamentos;
