// src/pages/ConfigAll.js
import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import authService from "../services/authService";
import Modal from "../components/Modal";
import "../assets/ConfigAll.css";

const ConfigAll = () => {
  const [usuario, setUsuario] = useState(null);
  const [tipoPopup, setTipoPopup] = useState(null);

  useEffect(() => {
    const local = JSON.parse(localStorage.getItem("usuario"));
    if (local?.id) {
      authService.getUserById(local.id)
        .then(res => setUsuario(res.data))
        .catch(err => console.error("Erro ao buscar usuário:", err));
    }
  }, []);

  const abrirPopup = (tipo) => setTipoPopup(tipo);
  const fecharPopup = () => setTipoPopup(null);

  return (
    <Layout>
      <div className="config-all-container">
        <h2>Informações do Usuário</h2>

        {usuario && (
          <div className="info-card">
            <div className="info-item">
              <span><strong>Nome:</strong> {usuario.nome}</span>
            </div>
            <div className="info-item">
              <span><strong>Email:</strong> {usuario.email}</span>
              <button onClick={() => abrirPopup("email")}>Editar</button>
            </div>
            <div className="info-item">
              <span><strong>Senha:</strong> ********</span>
              <button onClick={() => abrirPopup("senha")}>Alterar</button>
            </div>
            <div className="info-item">
              <span><strong>Rendimento:</strong> {usuario.renda.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })}</span>
              <button onClick={() => abrirPopup("renda")}>Editar</button>
            </div>
          </div>
        )}

        <Modal tipo={tipoPopup} isOpen={!!tipoPopup} onClose={fecharPopup} usuario={usuario} />
      </div>
    </Layout>
  );
};

export default ConfigAll;
