// src/pages/AlterarRendimento.js
import React, { useState } from "react";
import Layout from "../components/Layout";
import authService from "../services/authService";
import "../assets/Config.css";

const AlterarRendimento = () => {
  const [rendimento, setRendimento] = useState("");
  const [mensagem, setMensagem] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const usuarioId = localStorage.getItem("usuarioId");
    try {
      await authService.updateUser({ id: usuarioId, rendimento });
      setMensagem("Rendimento alterado com sucesso!");
    } catch (error) {
      console.error("Erro ao alterar o rendimento:", error);
      setMensagem("Erro ao alterar o rendimento.");
    }
  };

  return (
    <Layout>
      <div className="config-container">
        <h2>Alterar Rendimento</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="rendimento">Novo Rendimento:</label>
          <input
            type="number"
            id="rendimento"
            value={rendimento}
            required
            onChange={(e) => setRendimento(e.target.value)}
          />
          <button type="submit">Salvar</button>
        </form>
        {mensagem && <p className="mensagem">{mensagem}</p>}
      </div>
    </Layout>
  );
};

export default AlterarRendimento;
