import React, { useEffect, useState, useCallback, useRef } from "react";
import Layout from "../components/Layout";
import authService from "../services/authService";
import "../assets/Lancamentos.css"; // Certifique-se de que este CSS existe e está correto

const Lancamentos = () => {
  const [lancamentos, setLancamentos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Estados para os valores dos filtros
  const [filtroCategoria, setFiltroCategoria] = useState("");
  const [filtroSubcategoria, setFiltroSubcategoria] = useState("");
  const [filtroData, setFiltroData] = useState("");

  // Estados para controlar qual filtro está aberto
  const [openFilter, setOpenFilter] = useState(null); // 'categoria', 'subcategoria', 'data' ou null

  // Refs para detectar cliques fora dos filtros
  const categoryFilterRef = useRef(null);
  const subcategoryFilterRef = useRef(null);
  const dateFilterRef = useRef(null);

  const formatarValor = (valor) => {
    return valor.toLocaleString("pt-BR", { style: "currency", currency: "BRL" });
  };

  const carregarLancamentos = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const usuario = JSON.parse(localStorage.getItem("usuario"));
      if (!usuario || !usuario.id) {
        throw new Error("ID do usuário não encontrado. Faça login novamente.");
      }

      const response = await authService.buscarLancamentosPorUsuario(usuario.id);
      setLancamentos(response.data);
    } catch (err) {
      console.error("Erro ao carregar lançamentos:", err);
      if (err.response && err.response.data && err.response.data.message) {
        setError(`Erro ao carregar lançamentos: ${err.response.data.message}`);
      } else {
        setError("Não foi possível carregar os lançamentos. Verifique sua conexão.");
      }
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    carregarLancamentos();
  }, [carregarLancamentos]);

  // Efeito para detectar cliques fora dos filtros e fechá-los
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (openFilter === 'categoria' && categoryFilterRef.current && !categoryFilterRef.current.contains(event.target)) {
        setOpenFilter(null);
      } else if (openFilter === 'subcategoria' && subcategoryFilterRef.current && !subcategoryFilterRef.current.contains(event.target)) {
        setOpenFilter(null);
      } else if (openFilter === 'data' && dateFilterRef.current && !dateFilterRef.current.contains(event.target)) {
        setOpenFilter(null);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [openFilter]); // Depende de `openFilter` para reavaliar os listeners

  // Função para abrir/fechar um filtro específico
  const toggleFilter = (filterName) => {
    setOpenFilter(openFilter === filterName ? null : filterName);
  };

  // Lógica de filtragem dos lançamentos
  const lancamentosFiltrados = lancamentos.filter((lancamento) => {
    const categoriaLower = lancamento.categoria ? lancamento.categoria.toLowerCase() : '';
    const subcategoriaLower = lancamento.subcategoria ? lancamento.subcategoria.toLowerCase() : '';
    const dataLancamento = lancamento.data || ''; // Garante que dataLancamento seja uma string vazia se null/undefined

    const matchCategoria = filtroCategoria
      ? categoriaLower.includes(filtroCategoria.toLowerCase())
      : true;
    const matchSubcategoria = filtroSubcategoria
      ? subcategoriaLower.includes(filtroSubcategoria.toLowerCase())
      : true;
    const matchData = filtroData ? dataLancamento.includes(filtroData) : true;

    return matchCategoria && matchSubcategoria && matchData;
  });

  const handleExcluir = async (id) => {
    if (window.confirm("Tem certeza que deseja excluir este lançamento?")) {
      try {
        await authService.deletarGasto(id);
        carregarLancamentos();
        alert("Lançamento excluído com sucesso!");
      } catch (err) {
        console.error("Erro ao excluir lançamento:", err);
        alert("Erro ao excluir lançamento. Tente novamente.");
      }
    }
  };

  const handleEditar = (lancamento) => {
    alert("Funcionalidade de edição em desenvolvimento para: " + lancamento.subcategoria);
    // Aqui você abriria um modal ou navegaria para uma página de edição
    // e passaria o objeto 'lancamento' para ser editado.
  };

  return (
    <Layout>
      <div className="lancamentos-container">
        <h1 className="lancamentos-header">Lançamentos</h1>
        <div className="lancamentos-actions">
          <button className="novo-lancamento-button">+ Novo Lançamento</button>
        </div>

        {loading && (
          <div className="lancamentos-message loading-message">
            <p>Carregando lançamentos...</p>
          </div>
        )}

        {error && (
          <div className="lancamentos-message error-message">
            <p>{error}</p>
          </div>
        )}

        {!loading && !error && lancamentos.length === 0 && (
          <div className="lancamentos-message no-data-found-message">
            <p>Nenhum lançamento encontrado. Adicione um novo lançamento para começar!</p>
          </div>
        )}

        {!loading && !error && lancamentos.length > 0 && (
          <table className="lancamentos-table">
            <thead>
              <tr>
                <th>Valor</th>
                <th className="filter-header" onClick={() => toggleFilter('categoria')}>
                  Categoria
                  {openFilter === 'categoria' && (
                    <input
                      ref={categoryFilterRef}
                      type="text"
                      placeholder="Filtrar Categoria"
                      value={filtroCategoria}
                      onChange={(e) => setFiltroCategoria(e.target.value)}
                      onClick={(e) => e.stopPropagation()} // Impede que o clique no input feche o filtro
                      autoFocus // Foca automaticamente no input ao abrir
                    />
                  )}
                </th>
                <th className="filter-header" onClick={() => toggleFilter('subcategoria')}>
                  Subcategoria
                  {openFilter === 'subcategoria' && (
                    <input
                      ref={subcategoryFilterRef}
                      type="text"
                      placeholder="Filtrar Subcategoria"
                      value={filtroSubcategoria}
                      onChange={(e) => setFiltroSubcategoria(e.target.value)}
                      onClick={(e) => e.stopPropagation()}
                      autoFocus
                    />
                  )}
                </th>
                <th className="filter-header" onClick={() => toggleFilter('data')}>
                  Data
                  {openFilter === 'data' && (
                    <input
                      ref={dateFilterRef}
                      type="text"
                      placeholder="Filtrar Data (DD/MM/AAAA)"
                      value={filtroData}
                      onChange={(e) => setFiltroData(e.target.value)}
                      onClick={(e) => e.stopPropagation()}
                      autoFocus
                    />
                  )}
                </th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {lancamentosFiltrados.map((lancamento) => (
                <tr key={lancamento.id}>
                  <td>{formatarValor(lancamento.valor)}</td>
                  <td>{lancamento.categoria}</td>
                  <td>{lancamento.subcategoria}</td>
                  <td>{lancamento.data}</td> {/* A data agora aparecerá aqui */}
                  <td className="actions-cell">
                    <button
                      className="edit-button"
                      onClick={() => handleEditar(lancamento)}
                    >
                      Editar
                    </button>
                    <button
                      className="exclude-button"
                      onClick={() => handleExcluir(lancamento.id)}
                    >
                      Excluir
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </Layout>
  );
};

export default Lancamentos;
