// src/pages/Lancamentos.js

import React, { useEffect, useState, useCallback } from "react";
import Layout from "../components/Layout";
import authService from "../services/authService";
import EditarModal from "../components/EditarModal";
import NovoModal from "../components/NovoModal";
import "../assets/Lancamentos.css";

const Lancamentos = () => {
  const [lancamentos, setLancamentos] = useState([]);
  const [paginaAtual, setPaginaAtual] = useState(1);
  const [modalAberto, setModalAberto] = useState(false);
  const [lancamentoSelecionado, setLancamentoSelecionado] = useState(null);
  const [novoModalAberto, setNovoModalAberto] = useState(false);
  const [idParaExcluir, setIdParaExcluir] = useState(null);

  const [filtrosVisiveis, setFiltrosVisiveis] = useState(false);

  // ESTADOS PARA OS VALORES DOS FILTROS (agora select)
  const [filtroCategoria, setFiltroCategoria] = useState("");
  const [filtroSubcategoria, setFiltroSubcategoria] = useState("");
  const [filtroData, setFiltroData] = useState(""); // Continua sendo input de texto

  // NOVOS ESTADOS PARA AS OPÇÕES DOS SELECTS
  const [opcoesCategorias, setOpcoesCategorias] = useState([]);
  const [opcoesSubcategorias, setOpcoesSubcategorias] = useState([]);

  const itensPorPagina = 15;

  const categoriaMapeada = {
    NECESSIDADES: "Necessidade",
    DESEJOS: "Desejo",
    INVESTIMENTO_E_POUPANCA: "Investimento/Poupança"
  };

  // Função para formatar a data para DD/MM/AAAA
  const formatarData = (dataHoraString) => {
    if (!dataHoraString) return '';
    try {
      // Tenta analisar como ISO 8601 ou o formato que o backend retorna.
      // Se for apenas "AAAA-MM-DD", pode ser necessário ajustar a criação do Date.
      const date = new Date(dataHoraString);
      const dia = String(date.getDate()).padStart(2, '0');
      const mes = String(date.getMonth() + 1).padStart(2, '0'); // Mês é 0-indexed
      const ano = date.getFullYear();
      return `${dia}/${mes}/${ano}`;
    } catch (e) {
      console.error("Erro ao formatar data:", dataHoraString, e);
      return dataHoraString; // Retorna a string original se houver erro
    }
  };

  const buscarLancamentos = useCallback(() => {
    const usuario = JSON.parse(localStorage.getItem("usuario"));
    if (usuario?.id) {
      authService.buscarLancamentosPorUsuario(usuario.id)
        .then(res => {
          const dadosRecebidos = res.data;

          // Processar categorias e subcategorias únicas
          const categoriasUnicasSet = new Set();
          const subcategoriasUnicasSet = new Set();

          dadosRecebidos.forEach(item => {
            if (item.categoria) {
              categoriasUnicasSet.add(categoriaMapeada[item.categoria] || item.categoria);
            }
            if (item.subcategoria) {
              subcategoriasUnicasSet.add(item.subcategoria);
            }
            // Formatar a data para exibição ao buscar
            item.dataHoraExibicao = formatarData(item.dataHora);
          });

          setOpcoesCategorias(['Todas', ...Array.from(categoriasUnicasSet).sort()]);
          setOpcoesSubcategorias(['Todas', ...Array.from(subcategoriasUnicasSet).sort()]);

          setLancamentos(dadosRecebidos);
        })
        .catch(err => console.error("Erro ao buscar lançamentos", err));
    }
  }, []);

  useEffect(() => {
    buscarLancamentos();
  }, [buscarLancamentos]);

  // Lógica de FILTRAGEM
  const lancamentosFiltrados = lancamentos.filter((lancamento) => {
    const categoriaParaComparar = categoriaMapeada[lancamento.categoria] || lancamento.categoria;
    const subcategoriaParaComparar = lancamento.subcategoria;
    const dataParaComparar = lancamento.dataHoraExibicao; // Usar a data formatada para filtro

    const matchCategoria =
      filtroCategoria === "" || filtroCategoria === "Todas"
        ? true
        : categoriaParaComparar === filtroCategoria;

    const matchSubcategoria =
      filtroSubcategoria === "" || filtroSubcategoria === "Todas"
        ? true
        : subcategoriaParaComparar === filtroSubcategoria;

    const matchData = filtroData
      ? dataParaComparar.toLowerCase().includes(filtroData.toLowerCase())
      : true;

    return matchCategoria && matchSubcategoria && matchData;
  });

  // A PAGINAÇÃO é aplicada SOBRE os lançamentos JÁ FILTRADOS
  const totalPaginas = Math.ceil(lancamentosFiltrados.length / itensPorPagina);
  const lancamentosExibidos = lancamentosFiltrados.slice(
    (paginaAtual - 1) * itensPorPagina,
    paginaAtual * itensPorPagina
  );

  const mudarPagina = (novaPagina) => {
    if (novaPagina >= 1 && novaPagina <= totalPaginas) {
      setPaginaAtual(novaPagina);
    }
  };

  const confirmarExclusao = (id) => {
    setIdParaExcluir(id);
  };

  const cancelarExclusao = () => {
    setIdParaExcluir(null);
  };

  const excluirConfirmado = async () => {
    try {
      await authService.deletarGasto(idParaExcluir);
      buscarLancamentos();
      setIdParaExcluir(null);
      setPaginaAtual(1);
    } catch (error) {
      alert("Erro ao excluir lançamento.");
      console.error("Erro ao excluir gasto:", error);
    }
  };

  const abrirModal = (lancamento) => {
    setLancamentoSelecionado({
      ...lancamento,
      dataHora: lancamento.dataHora // Passa a data original para o modal de edição
    });
    setModalAberto(true);
  };

  const salvarAlteracoes = async (lancamentoEditado) => {
    try {
      await authService.alterarGasto(lancamentoEditado.id, {
        valor: lancamentoEditado.valor,
        categoria: lancamentoEditado.categoria,
        subcategoria: lancamentoEditado.subcategoria,
        dataHora: lancamentoEditado.dataHora, // Certifique-se que o formato enviado ao backend é o esperado (ISO ou outro)
      });
      buscarLancamentos();
      setModalAberto(false);
    } catch (error) {
      console.error("Erro ao salvar alteração:", error);
      alert("Erro ao salvar alteração no lançamento.");
    }
  };


  return (
    <Layout>
      <div className="lancamentos-container">
        <div className="header-bar">
          <h2>Lançamentos</h2>
          <div className="header-buttons">
            <button className="novo-button" onClick={() => setNovoModalAberto(true)}>
              + Novo Lançamento
            </button>
            <button
              className="toggle-filtros-button"
              onClick={() => setFiltrosVisiveis(!filtrosVisiveis)}
            >
              {filtrosVisiveis ? "Esconder Filtros" : "Mostrar Filtros"}
            </button>
          </div>
        </div>

        <table className="lancamentos-table">
          <thead>
            <tr>
              <th>Valor</th>
              <th>Categoria</th>
              <th>Subcategoria</th>
              <th>Data</th>
              <th>Ações</th>
            </tr>
            {/* LINHA DE FILTROS CONDICIONAL */}
            {filtrosVisiveis && (
              <tr className="filter-row">
                <td>{/* Coluna Valor sem filtro */}</td>
                <td>
                  <select
                    value={filtroCategoria}
                    onChange={(e) => {
                      setFiltroCategoria(e.target.value);
                      setPaginaAtual(1);
                    }}
                  >
                    {opcoesCategorias.map((opcao) => (
                      <option key={opcao} value={opcao}>
                        {opcao}
                      </option>
                    ))}
                  </select>
                </td>
                <td>
                  <select
                    value={filtroSubcategoria}
                    onChange={(e) => {
                      setFiltroSubcategoria(e.target.value);
                      setPaginaAtual(1);
                    }}
                  >
                    {opcoesSubcategorias.map((opcao) => (
                      <option key={opcao} value={opcao}>
                        {opcao}
                      </option>
                    ))}
                  </select>
                </td>
                <td>
                  <input
                    type="text"
                    placeholder="Filtrar Data (DD/MM/AAAA)"
                    value={filtroData}
                    onChange={(e) => {
                      setFiltroData(e.target.value);
                      setPaginaAtual(1);
                    }}
                  />
                </td>
                <td>{/* Coluna Ações sem filtro */}</td>
              </tr>
            )}
          </thead>
          <tbody>
            {lancamentosExibidos.length === 0 ? (
              <tr>
                <td colSpan="5" className="no-results-message">
                  Nenhum lançamento encontrado com os filtros aplicados.
                </td>
              </tr>
            ) : (
              lancamentosExibidos.map((item) => (
                <tr key={item.id}>
                  <td>{item.valor.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })}</td>
                  <td>{categoriaMapeada[item.categoria] || item.categoria}</td>
                  <td>{item.subcategoria}</td>
                  <td>{item.dataHoraExibicao}</td> {/* Usando a data formatada para exibição */}
                  <td className="acoes">
                    <button className="editar" onClick={() => abrirModal(item)}>Editar</button>
                    <button className="excluir" onClick={() => confirmarExclusao(item.id)}>Excluir</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>

        <div className="paginacao">
          <button onClick={() => mudarPagina(paginaAtual - 1)} disabled={paginaAtual === 1}>Anterior</button>
          <span>Página {paginaAtual} de {totalPaginas === 0 ? 1 : totalPaginas}</span>
          <button onClick={() => mudarPagina(paginaAtual + 1)} disabled={paginaAtual === totalPaginas || totalPaginas === 0}>Próxima</button>
        </div>
      </div>

      {/* Modais e popups permanecem inalterados */}
      {modalAberto && (
        <EditarModal
          lancamento={lancamentoSelecionado}
          onClose={() => setModalAberto(false)}
          onSave={salvarAlteracoes}
        />
      )}

      {novoModalAberto && (
        <NovoModal
          onClose={() => setNovoModalAberto(false)}
          onSalvarNovo={(idUsuario, novoGasto) => {
            authService.cadastrarGasto(idUsuario, novoGasto)
              .then(() => {
                buscarLancamentos();
                setNovoModalAberto(false);
              })
              .catch(err => {
                console.error("Erro ao cadastrar gasto:", err);
                alert("Erro ao cadastrar novo lançamento.");
              });
          }}
        />
      )}

      {idParaExcluir !== null && (
        <div className="popup-overlay">
          <div className="popup">
            <p>Tem certeza que deseja excluir este lançamento?</p>
            <div className="popup-buttons">
              <button className="btn-confirmar" onClick={excluirConfirmado}>Confirmar</button>
              <button className="btn-cancelar" onClick={cancelarExclusao}>Cancelar</button>
            </div>
          </div>
        </div>
      )}
    </Layout>
  );
};

export default Lancamentos;
