// src/pages/Home.js
import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import "../assets/Home.css"; // Certifique-se que o CSS está linkado
import { Chart } from "react-google-charts"; // Importa Chart
import authService from "../services/authService";

// Dados que o Google Charts pode usar para um estado de 'carregando'
export const data = [
  ["Task", "Hours per Day"],
  ["", 0],
];

export const options = {
  title: "Carregando dados...",
  pieHole: 0.4,
  is3D: false,
};

const Home = () => {
  const [renda, setRenda] = useState(0);
  const [gastos, setGastos] = useState([]);
  const [totalGasto, setTotalGasto] = useState(0);
  const [totalInvestimento, setTotalInvestimento] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState("NECESSIDADES");
  const [chartsLoaded, setChartsLoaded] = useState(false); // Novo estado para controlar o carregamento dos gráficos

  useEffect(() => {
    const usuario = JSON.parse(localStorage.getItem("usuario"));
    if (usuario?.id) {
      authService.getUserById(usuario.id)
        .then(res => setRenda(res.data.renda || 0))
        .catch(err => console.error("Erro ao buscar usuário:", err));

      authService.buscarLancamentosPorUsuario(usuario.id)
        .then(res => {
          const todos = res.data;
          setGastos(todos);

          const totalGasto = todos
            .filter(g => g.categoria !== "INVESTIMENTO_E_POUPANCA")
            .reduce((acc, g) => acc + g.valor, 0);

          const totalInvest = todos
            .filter(g => g.categoria === "INVESTIMENTO_E_POUPANCA")
            .reduce((acc, g) => acc + g.valor, 0);

          setTotalGasto(totalGasto);
          setTotalInvestimento(totalInvest);
          setChartsLoaded(true); // Dados carregados, podemos tentar renderizar os gráficos
        })
        .catch(err => {
          console.error("Erro ao buscar lançamentos:", err);
          setGastos([]);
          setTotalGasto(0);
          setTotalInvestimento(0);
          setChartsLoaded(true); // Mesmo com erro, tentamos renderizar com dados vazios
        });
    } else {
        setChartsLoaded(true); // Se não há usuário, também permitimos o render com dados vazios
    }
  }, []);

  const categoryColors = {
    "investimento e poupanca": "#28a745", // Verde
    "necessidades": "#007bff",          // Azul
    "desejos": "#dc3545",                // Vermelho
  };

  const dadosGraficoBrutos = gastos.reduce((acc, item) => {
    const nome = item.categoria
      .replace("_E_", " e ")
      .replace(/_/g, " ")
      .toLowerCase();
    const existente = acc.find(i => i[0] === nome);
    if (existente) {
      existente[1] += item.valor;
    } else {
      acc.push([nome, item.valor]);
    }
    return acc;
  }, []);

  // Garante que o gráfico de pizza sempre tenha dados válidos
  const dadosGrafico = [
    ["Categoria", "Valor"],
    ...(dadosGraficoBrutos.length > 0 ? dadosGraficoBrutos : [["Nenhuma Categoria", 0]])
  ];

  const agruparSubcategorias = (lista, categoria) => {
    const filtrado = lista.filter(g => g.categoria === categoria);
    
    const agrupado = {};
    filtrado.forEach(g => {
      // Garante que o valor é um número para evitar NaN ou undefined
      agrupado[g.subcategoria] = (agrupado[g.subcategoria] || 0) + (parseFloat(g.valor) || 0);
    });

    const resultados = Object.entries(agrupado).map(([nome, valor]) => {
        const totalCategoria = filtrado.reduce((acc, g) => acc + Math.abs(parseFloat(g.valor) || 0), 0);
        const percentual = totalCategoria > 0 ? `${((Math.abs(valor) / totalCategoria) * 100).toFixed(1)}%` : '0%';
        return [
            nome,
            valor,
            percentual // Annotation para o percentual
        ];
    });

    if (resultados.length === 0) {
        // Retorna dados válidos para um gráfico vazio, incluindo o role 'annotation'
        return [["Nenhuma Subcategoria", 0, "0%"]]; 
    }
    return resultados;
  };


  const renderCategoryChart = () => {
    let title = "";
    let description = "";
    const dataHeader = ["Subcategoria", "Valor", { role: "annotation" }]; 
    let colors = ["#007bff"]; 

    switch (selectedCategory) {
      case "NECESSIDADES":
        title = "Necessidades: Onde seu dinheiro é essencial";
        description = "Detalhes dos seus gastos fixos e indispensáveis.";
        colors = ["#007bff"];
        break;
      case "DESEJOS":
        title = "Desejos: Gastos para o seu lazer e prazer";
        description = "Visão detalhada dos seus gastos não essenciais e de lazer.";
        colors = ["#dc3545"];
        break;
      case "INVESTIMENTO_E_POUPANCA":
        title = "Investimentos/Poupança: Construindo seu futuro";
        description = "Acompanhe seu progresso em direção aos seus objetivos financeiros.";
        colors = ["#28a745"];
        break;
      default:
        return null;
    }

    const subcategoryData = agruparSubcategorias(gastos, selectedCategory);
    const chartData = [dataHeader, ...subcategoryData];

    // Verifica se os dados estão vazios (apenas cabeçalho ou "Sem dados" padrão)
    const isEmptyChart = chartData.length <= 1 || (chartData.length === 2 && chartData[1][0] === "Nenhuma Subcategoria");

    return (
      <div className="chart-card">
        <div className="chart-buttons-container">
          <button
            className={`chart-button ${selectedCategory === "NECESSIDADES" ? "active" : ""}`}
            onClick={() => setSelectedCategory("NECESSIDADES")}
          >
            Necessidades
          </button>
          <button
            className={`chart-button ${selectedCategory === "DESEJOS" ? "active" : ""}`}
            onClick={() => setSelectedCategory("DESEJOS")}
          >
            Desejos
          </button>
          <button
            className={`chart-button ${selectedCategory === "INVESTIMENTO_E_POUPANCA" ? "active" : ""}`}
            onClick={() => setSelectedCategory("INVESTIMENTO_E_POUPANCA")}
          >
            Investimentos
          </button>
        </div>
        <h2 className="chart-title">{title}</h2>
        <p className="chart-description">{description}</p>
        <div className="chart-wrapper">
          {!chartsLoaded ? ( // Mostra um loader enquanto os dados não estão carregados
            <p className="loading-message">Carregando dados do gráfico...</p>
          ) : isEmptyChart ? (
            <p className="no-data-message">Não há dados para esta categoria.</p>
          ) : (
            <Chart
              chartType="BarChart"
              width="100%"
              height="100%"
              data={chartData}
              options={{
                title: "",
                legend: { position: "none" },
                bars: "horizontal",
                hAxis: { 
                  format: "currency", 
                  textStyle: { color: '#555', fontName: 'Inter' },
                  minValue: 0,
                },
                vAxis: { textStyle: { color: '#555', fontName: 'Inter' } },
                colors: colors,
                chartArea: { left: 100, top: 30, right: 30, bottom: 30 },
                fontName: 'Inter, sans-serif',
                titleTextStyle: {
                  fontSize: 0,
                },
                tooltip: { textStyle: { fontName: 'Inter', fontSize: 13 } },
                annotations: {
                  alwaysOutside: true,
                  textStyle: {
                    fontSize: 12,
                    color: '#000',
                    fontName: 'Inter',
                    auraColor: 'none'
                  }
                },
                animation: {
                  startup: true,
                  easing: 'out',
                  duration: 500
                }
              }}
              key={selectedCategory} 
            />
          )}
        </div>
      </div>
    );
  };

  return (
    <Layout>
      <div className="home-container">
        <h1 className="home-header">Relatórios Financeiros</h1>

        <div className="top-cards">
          <div className="stat-card">
            <span className="card-label">Rendimento Total</span>
            <span className="card-value">
              {renda.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })}
            </span>
          </div>
          <div className="stat-card">
            <span className="card-label">Gastos Totais</span>
            <span className="card-value">
              {totalGasto.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })}
            </span>
          </div>
          <div className="stat-card">
            <span className="card-label">Valor Investido</span>
            <span className="card-value">
              {totalInvestimento.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })}
            </span>
          </div>
        </div>

        <div className="dashboard-grid">
          {/* Gráfico 1: Pizza geral */}
          <div className="chart-card">
            <h2 className="chart-title">Distribuição de Gastos por Categoria</h2>
            <p className="chart-description">Entenda para onde seu dinheiro está indo, dividido em necessidades, desejos e investimentos.</p>
            <div className="chart-wrapper">
                {!chartsLoaded ? (
                    <p className="loading-message">Carregando dados do gráfico...</p>
                ) : (
                    <Chart
                    chartType="PieChart"
                    width="100%"
                    height="100%"
                    data={dadosGrafico}
                    options={{
                        pieHole: 0.4,
                        colors: dadosGrafico.slice(1).map(item => categoryColors[item[0]] || '#CCCCCC'),
                        legend: { position: "bottom", textStyle: { color: '#555', fontName: 'Inter', fontSize: 13 } },
                        chartArea: { left: 15, top: 15, right: 15, bottom: 50 },
                        fontName: 'Inter, sans-serif',
                        titleTextStyle: {
                        fontSize: 0,
                        },
                        tooltip: { textStyle: { fontName: 'Inter', fontSize: 13 } },
                        slices: dadosGrafico.length === 2 && dadosGrafico[1][0] === "Nenhuma Categoria" ? {
                            0: { color: '#CCCCCC' }
                        } : {},
                        animation: {
                            startup: true,
                            easing: 'out',
                            duration: 500
                        }
                    }}
                    />
                )}
            </div>
          </div>

          {/* Gráfico de categoria selecionada (substitui os 3 gráficos anteriores) */}
          {renderCategoryChart()}
        </div>
      </div>
    </Layout>
  );
};

export default Home;
