import React, { useEffect, useState } from 'react';
import authService from '../services/authService';
import Modal from './Modal';
import '../assets/ConfigAll.css';

const ConfigAll = () => {
  const [usuario, setUsuario] = useState({});
  const [modalAberto, setModalAberto] = useState(false);
  const [tipoEdicao, setTipoEdicao] = useState('');

  const abrirModal = (tipo) => {
    setTipoEdicao(tipo);
    setModalAberto(true);
  };

  const fecharModal = () => setModalAberto(false);

  useEffect(() => {
    const usuarioId = localStorage.getItem('usuarioId');
    if (usuarioId) {
      authService.getUserById(usuarioId).then(setUsuario);
    }
  }, []);

  return (
    <div className="config-container">
      <div className="config-card">
        <h2>Informações do Usuário</h2>
        <div className="config-info">
          <div className="config-row">
            <span className="config-label">Nome:</span>
            <span className="config-value">{usuario.nome}</span>
          </div>
          <div className="config-row">
            <span className="config-label">Email:</span>
            <span className="config-value">{usuario.email}</span>
            <button className="edit-button" onClick={() => abrirModal('email')}>Editar</button>
          </div>
          <div className="config-row">
            <span className="config-label">Senha:</span>
            <span className="config-value">********</span>
            <button className="edit-button" onClick={() => abrirModal('senha')}>Alterar</button>
          </div>
          <div className="config-row">
            <span className="config-label">Rendimento:</span>
            <span className="config-value">R$ {usuario.rendimento?.toLocaleString('pt-BR')}</span>
            <button className="edit-button" onClick={() => abrirModal('renda')}>Editar</button>
          </div>
        </div>
      </div>

      {modalAberto && (
        <Modal tipo={tipoEdicao} onClose={fecharModal} usuario={usuario} onAtualizar={setUsuario} />
      )}
    </div>
  );
};

export default ConfigAll;
