import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom"; // Use navigate para redirecionar para outras páginas
import "../assets/Login.css"; // Importa o CSS da página de login

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate(); // Para redirecionar programaticamente

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("E-mail:", email, "Senha:", password);
    // Aqui você poderia adicionar a lógica para autenticar o usuário.
    // Se a autenticação for bem-sucedida, redirecione para a página principal ou home.
    navigate("/home"); // Redireciona para a página home (ajuste conforme sua navegação)
  };

  return (
    <div className="container">
      <div className="card">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="email">E-mail:</label>
            <input
              type="email"
              id="email"
              className="input"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <label htmlFor="password">Senha:</label>
            <input
              type="password"
              id="password"
              className="input"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <button className="button" type="submit">
            Entrar
          </button>
        </form>

        <p>
          Não tem uma conta? <Link to="/registro">Crie uma aqui</Link> {/* Link para página de registro */}
        </p>
      </div>
    </div>
  );
};

export default Login;
