import React, { useEffect, useState, useCallback } from "react";
import Layout from "../components/Layout";
import authService from "../services/authService";
import "../assets/Lancamentos.css"; // Certifique-se de que este CSS existe e está correto

const Lancamentos = () => {
  const [lancamentos, setLancamentos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filtroCategoria, setFiltroCategoria] = useState("");
  const [filtroSubcategoria, setFiltroSubcategoria] = useState("");
  const [filtroData, setFiltroData] = useState("");

  // Estados para controlar a visibilidade dos selects de filtro
  const [showFilterCategoria, setShowFilterCategoria] = useState(false);
  const [showFilterSubcategoria, setShowFilterSubcategoria] = useState(false);
  const [showFilterData, setShowFilterData] = useState(false);

  const formatarValor = (valor) => {
    return valor.toLocaleString("pt-BR", { style: "currency", currency: "BRL" });
  };

  const carregarLancamentos = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const usuario = JSON.parse(localStorage.getItem("usuario"));
      if (!usuario || !usuario.id) {
        throw new Error("ID do usuário não encontrado. Faça login novamente.");
      }

      // O endpoint para buscar lançamentos por usuário é /gasto/buscar/categoria?idUsuario={id}
      const response = await authService.buscarLancamentosPorUsuario(usuario.id);
      setLancamentos(response.data);
    } catch (err) {
      console.error("Erro ao carregar lançamentos:", err);
      if (err.response && err.response.data && err.response.data.message) {
        setError(`Erro ao carregar lançamentos: ${err.response.data.message}`);
      } else {
        setError("Não foi possível carregar os lançamentos. Verifique sua conexão.");
      }
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    carregarLancamentos();
  }, [carregarLancamentos]);

  // Função para abrir/fechar o filtro de Categoria
  const toggleFilterCategoria = () => {
    setShowFilterCategoria(!showFilterCategoria);
    // Fechar outros filtros para evitar múltiplos selects abertos
    setShowFilterSubcategoria(false);
    setShowFilterData(false);
  };

  // Função para abrir/fechar o filtro de Subcategoria
  const toggleFilterSubcategoria = () => {
    setShowFilterSubcategoria(!showFilterSubcategoria);
    setShowFilterCategoria(false);
    setShowFilterData(false);
  };

  // Função para abrir/fechar o filtro de Data
  const toggleFilterData = () => {
    setShowFilterData(!showFilterData);
    setShowFilterCategoria(false);
    setShowFilterSubcategoria(false);
  };

  // Coleta de valores únicos para os filtros
  const categoriasUnicas = Array.from(new Set(lancamentos.map((l) => l.categoria)));
  const subcategoriasUnicas = Array.from(new Set(lancamentos.map((l) => l.subcategoria)));
  const datasUnicas = Array.from(new Set(lancamentos.map((l) => l.data))); // Assumindo 'data' já está no formato de string desejado

  // Lógica de filtragem dos lançamentos
  const lancamentosFiltrados = lancamentos.filter((lancamento) => {
    const matchCategoria = filtroCategoria
      ? lancamento.categoria === filtroCategoria
      : true;
    const matchSubcategoria = filtroSubcategoria
      ? lancamento.subcategoria === filtroSubcategoria
      : true;
    const matchData = filtroData ? lancamento.data === filtroData : true;

    return matchCategoria && matchSubcategoria && matchData;
  });

  // Função para lidar com exclusão (placeholder)
  const handleExcluir = async (id) => {
    if (window.confirm("Tem certeza que deseja excluir este lançamento?")) {
      try {
        await authService.deletarGasto(id); // Chamando o endpoint de deleção
        // Recarregar os lançamentos após a exclusão
        carregarLancamentos();
        alert("Lançamento excluído com sucesso!");
      } catch (err) {
        console.error("Erro ao excluir lançamento:", err);
        alert("Erro ao excluir lançamento. Tente novamente.");
      }
    }
  };

  // Função para lidar com edição (placeholder) - você precisará de um modal/formulário para isso
  const handleEditar = (lancamento) => {
    alert("Funcionalidade de edição em desenvolvimento para: " + lancamento.subcategoria);
    // Aqui você abriria um modal ou navegaria para uma página de edição
    // e passaria o objeto 'lancamento' para ser editado.
  };

  return (
    <Layout>
      <div className="lancamentos-container">
        <h1 className="lancamentos-header">Lançamentos</h1>
        <div className="lancamentos-actions">
          <button className="novo-lancamento-button">+ Novo Lançamento</button>
        </div>

        {loading && (
          <div className="lancamentos-message loading-message">
            <p>Carregando lançamentos...</p>
          </div>
        )}

        {error && (
          <div className="lancamentos-message error-message">
            <p>{error}</p>
          </div>
        )}

        {!loading && !error && lancamentos.length === 0 && (
          <div className="lancamentos-message no-data-found-message">
            <p>Nenhum lançamento encontrado. Adicione um novo lançamento para começar!</p>
          </div>
        )}

        {!loading && !error && lancamentos.length > 0 && (
          <table className="lancamentos-table">
            <thead>
              <tr>
                <th>Valor</th>
                <th className="filter-header" onClick={toggleFilterCategoria}>
                  Categoria{" "}
                  {showFilterCategoria && (
                    <select
                      value={filtroCategoria}
                      onChange={(e) => setFiltroCategoria(e.target.value)}
                      onClick={(e) => e.stopPropagation()} // Impede que o clique no select feche ele
                    >
                      <option value="">Todas</option>
                      {categoriasUnicas.map((categoria) => (
                        <option key={categoria} value={categoria}>
                          {categoria}
                        </option>
                      ))}
                    </select>
                  )}
                </th>
                <th className="filter-header" onClick={toggleFilterSubcategoria}>
                  Subcategoria{" "}
                  {showFilterSubcategoria && (
                    <select
                      value={filtroSubcategoria}
                      onChange={(e) => setFiltroSubcategoria(e.target.value)}
                      onClick={(e) => e.stopPropagation()}
                    >
                      <option value="">Todas</option>
                      {subcategoriasUnicas.map((subcategoria) => (
                        <option key={subcategoria} value={subcategoria}>
                          {subcategoria}
                        </option>
                      ))}
                    </select>
                  )}
                </th>
                <th className="filter-header" onClick={toggleFilterData}>
                  Data{" "}
                  {showFilterData && (
                    <select
                      value={filtroData}
                      onChange={(e) => setFiltroData(e.target.value)}
                      onClick={(e) => e.stopPropagation()}
                    >
                      <option value="">Todas</option>
                      {datasUnicas.map((data) => (
                        <option key={data} value={data}>
                          {data}
                        </option>
                      ))}
                    </select>
                  )}
                </th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {lancamentosFiltrados.map((lancamento) => (
                <tr key={lancamento.id}>
                  <td>{formatarValor(lancamento.valor)}</td>
                  <td>{lancamento.categoria}</td>
                  <td>{lancamento.subcategoria}</td>
                  <td>{lancamento.data}</td>
                  <td className="actions-cell">
                    <button
                      className="edit-button"
                      onClick={() => handleEditar(lancamento)}
                    >
                      Editar
                    </button>
                    <button
                      className="exclude-button"
                      onClick={() => handleExcluir(lancamento.id)}
                    >
                      Excluir
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </Layout>
  );
};

export default Lancamentos;
