import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import authService from "../services/authService";
import "../assets/Config.css"; // Reaproveitando o estilo geral

const Lancamentos = () => {
  const [lancamentos, setLancamentos] = useState([]);
  const [pagina, setPagina] = useState(0);
  const itensPorPagina = 5;

  useEffect(() => {
    const usuario = JSON.parse(localStorage.getItem("usuario"));
    if (usuario?.id) {
      authService.buscarLancamentosPorUsuario(usuario.id)
        .then(res => setLancamentos(res.data))
        .catch(err => console.error("Erro ao buscar lançamentos", err));
    }
  }, []);

  const totalPaginas = Math.ceil(lancamentos.length / itensPorPagina);
  const lancamentosExibidos = lancamentos.slice(
    pagina * itensPorPagina,
    pagina * itensPorPagina + itensPorPagina
  );

  const handleExcluir = (id) => {
    if (window.confirm("Tem certeza que deseja excluir este lançamento?")) {
      console.log("Excluir lançamento:", id);
    }
  };

  const handleEditar = (id) => {
    console.log("Editar lançamento:", id);
  };

  return (
    <Layout>
      <div className="config-container" style={{ width: "95%", maxWidth: "1200px", minHeight: "auto" }}>
        <h2 style={{ textAlign: "center" }}>Lançamentos</h2>
        
        <div style={{ textAlign: "right", marginBottom: "20px" }}>
          <button className="salvar-btn" onClick={() => alert("Novo lançamento")}>
            Novo Lançamento
          </button>
        </div>

        <table className="lancamentos-tabela">
          <thead>
            <tr>
              <th>Valor</th>
              <th>Categoria</th>
              <th>Subcategoria</th>
              <th>Data</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {lancamentosExibidos.map((item, idx) => (
              <tr key={idx}>
                <td>R$ {item.valor.toFixed(2)}</td>
                <td>{item.categoria}</td>
                <td>{item.subcategoria}</td>
                <td>{item.data}</td>
                <td>
                  <button onClick={() => handleEditar(item.id)} className="editar-btn">Editar</button>
                  <button onClick={() => handleExcluir(item.id)} className="deletar-btn">Excluir</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="paginacao">
          <button
            className="anterior-btn"
            onClick={() => setPagina((prev) => Math.max(prev - 1, 0))}
            disabled={pagina === 0}
          >
            Anterior
          </button>
          <span>Página {pagina + 1} de {totalPaginas}</span>
          <button
            className="proxima-btn"
            onClick={() => setPagina((prev) => Math.min(prev + 1, totalPaginas - 1))}
            disabled={pagina + 1 >= totalPaginas}
          >
            Próxima
          </button>
        </div>
      </div>
    </Layout>
  );
};

export default Lancamentos;
