// src/pages/ConfigAll.js
import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";
import authService from "../services/authService";
import "../assets/ConfigAll.css";

const ConfigAll = () => {
  const id = localStorage.getItem("id");
  const [senhaAtual, setSenhaAtual] = useState("");
  const [novaSenha, setNovaSenha] = useState("");
  const [novoEmail, setNovoEmail] = useState("");
  const [emailSenha, setEmailSenha] = useState("");
  const [novoRendimento, setNovoRendimento] = useState("");

  const handleSalvarSenha = async () => {
    try {
      await authService.alterarSenha(id, {
        email: localStorage.getItem("email"),
        senhaAtual,
        novaSenha,
      });
      alert("Senha alterada com sucesso!");
    } catch (error) {
      console.error(error);
      alert("Erro ao alterar a senha.");
    }
  };

  const handleSalvarEmail = async () => {
    try {
      await authService.alterarEmail(id, novoEmail, emailSenha);
      alert("Email alterado com sucesso!");
    } catch (error) {
      console.error(error);
      alert("Erro ao alterar o email.");
    }
  };

  const handleSalvarRendimento = async () => {
    try {
      await authService.alterarRendimento(id, novoRendimento);
      alert("Rendimento alterado com sucesso!");
    } catch (error) {
      console.error(error);
      alert("Erro ao alterar o rendimento.");
    }
  };

  return (
    <Layout>
      <div className="config-all">
        <div className="config-card">
          <h2 className="config-title">Alterar Senha</h2>
          <input
            className="config-input"
            type="password"
            placeholder="Senha atual"
            value={senhaAtual}
            onChange={(e) => setSenhaAtual(e.target.value)}
          />
          <input
            className="config-input"
            type="password"
            placeholder="Nova senha"
            value={novaSenha}
            onChange={(e) => setNovaSenha(e.target.value)}
          />
          <button className="config-button" onClick={handleSalvarSenha}>
            Salvar
          </button>
        </div>

        <div className="config-card">
          <h2 className="config-title">Alterar E-mail</h2>
          <input
            className="config-input"
            type="email"
            placeholder="Novo e-mail"
            value={novoEmail}
            onChange={(e) => setNovoEmail(e.target.value)}
          />
          <input
            className="config-input"
            type="password"
            placeholder="Senha atual"
            value={emailSenha}
            onChange={(e) => setEmailSenha(e.target.value)}
          />
          <button className="config-button" onClick={handleSalvarEmail}>
            Salvar
          </button>
        </div>

        <div className="config-card">
          <h2 className="config-title">Alterar Rendimento</h2>
          <input
            className="config-input"
            type="number"
            placeholder="Novo rendimento"
            value={novoRendimento}
            onChange={(e) => setNovoRendimento(e.target.value)}
          />
          <button className="config-button" onClick={handleSalvarRendimento}>
            Salvar
          </button>
        </div>
      </div>
    </Layout>
  );
};

export default ConfigAll;
