import React, { useState } from "react";
import { Link } from "react-router-dom"; // Importa Link para navegação entre páginas
import "../assets/Login.css"; // Importa o CSS da página de login

const Login = () => {
  // Estados para armazenar os valores digitados nos campos de e-mail e senha
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Função chamada ao enviar o formulário de login
  const handleSubmit = (e) => {
    e.preventDefault(); // Impede o recarregamento da página
    console.log("E-mail:", email, "Senha:", password);
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {/* Formulário de login */}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">E-mail:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)} // Atualiza o estado ao digitar
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Senha:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} // Atualiza o estado ao digitar
            required
          />
        </div>

        <button type="submit">Entrar</button>
      </form>

      {/* Link para a página de registro */}
      <p>
        Não tem uma conta? <Link to="/registro">Registre-se aqui</Link>
      </p>
    </div>
  );
};

export default Login;
