import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import Modal from "../components/Modal";
import authService from "../services/authService";
import "../assets/ConfigAll.css"; // New CSS for this page

const ConfigAll = () => {
  const [usuario, setUsuario] = useState(null);
  const [modalAberto, setModalAberto] = useState(false);
  const [tipoEdicao, setTipoEdicao] = useState("");

  // State for form fields
  const [login, setLogin] = useState("");
  const [nome, setNome] = useState("");
  const [dataNascimento, setDataNascimento] = useState("");
  const [telefone, setTelefone] = useState("");
  const [celular, setCelular] = useState("");
  const [prestador, setPrestador] = useState("NÃO É PRESTADOR"); // Default value

  useEffect(() => {
    const usuarioStorage = JSON.parse(localStorage.getItem("usuario"));
    if (usuarioStorage?.id) {
      authService
        .getUserById(usuarioStorage.id)
        .then((res) => {
          setUsuario(res.data);
          // Initialize form fields with fetched user data
          setLogin(res.data.email || ""); // Assuming email is login
          setNome(res.data.nome || "");
          setDataNascimento(res.data.dataNascimento || ""); // Format if necessary
          setTelefone(res.data.telefone || "");
          setCelular(res.data.celular || "");
          setPrestador(res.data.prestador || "NÃO É PRESTADOR");
        })
        .catch((err) =>
          console.error("Erro ao buscar dados do usuário:", err)
        );
    }
  }, []);

  const abrirModal = (tipo) => {
    setTipoEdicao(tipo);
    setModalAberto(true);
  };

  const fecharModal = () => {
    setModalAberto(false);
    setTipoEdicao("");
  };

  const handleSave = () => {
    // Implement your logic to save updated user data to the backend
    console.log("Salvando dados do usuário:", {
      login,
      nome,
      dataNascimento,
      telefone,
      celular,
      prestador,
    });
    // Example: Call an authService method to update user
    // authService.updateUser({ ...usuario, nome, telefone, celular, prestador, dataNascimento })
    //   .then(res => {
    //     console.log('Usuário atualizado com sucesso!', res.data);
    //     setUsuario(res.data); // Update local state with new data
    //   })
    //   .catch(err => console.error('Erro ao atualizar usuário:', err));
  };

  if (!usuario) {
    return (
      <Layout>
        <div className="profile-container">Carregando informações...</div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="profile-container">
        <div className="profile-card">
          <h1 className="profile-header">Meu Perfil</h1>

          <div className="profile-nav">
            <span className="profile-nav-item active">Dados do Usuário</span>
            {/* Add more tabs here if needed */}
          </div>

          <div className="profile-content">
            <div className="profile-image-section">
              <img
                src="https://via.placeholder.com/150" // Replace with actual user image source if available
                alt="Perfil do Usuário"
                className="profile-avatar"
              />
            </div>

            <div className="profile-form">
              <div className="form-group">
                <label htmlFor="login">Login*</label>
                <input
                  type="email"
                  id="login"
                  value={login}
                  onChange={(e) => setLogin(e.target.value)}
                  readOnly // Login often isn't directly editable via input
                />
                <button
                  className="change-password-button"
                  onClick={() => abrirModal("senha")}
                >
                  Alterar senha
                </button>
              </div>

              <div className="form-group">
                <label htmlFor="nome">Nome*</label>
                <input
                  type="text"
                  id="nome"
                  value={nome}
                  onChange={(e) => setNome(e.target.value)}
                />
              </div>

              <div className="form-group">
                <label htmlFor="dataNascimento">Data de Nascimento</label>
                <input
                  type="date"
                  id="dataNascimento"
                  value={dataNascimento}
                  onChange={(e) => setDataNascimento(e.target.value)}
                />
              </div>

              <div className="form-group">
                <label htmlFor="telefone">Telefone</label>
                <input
                  type="tel"
                  id="telefone"
                  value={telefone}
                  onChange={(e) => setTelefone(e.target.value)}
                />
              </div>

              <div className="form-group">
                <label htmlFor="celular">Celular</label>
                <input
                  type="tel"
                  id="celular"
                  value={celular}
                  onChange={(e) => setCelular(e.target.value)}
                />
              </div>

              <div className="form-group">
                <label htmlFor="prestador">Prestador</label>
                <select
                  id="prestador"
                  value={prestador}
                  onChange={(e) => setPrestador(e.target.value)}
                >
                  <option value="NÃO É PRESTADOR">NÃO É PRESTADOR</option>
                  <option value="É PRESTADOR">É PRESTADOR</option>
                  {/* Add more options as needed */}
                </select>
              </div>

              <button className="save-button" onClick={handleSave}>
                Salvar
              </button>
            </div>
          </div>
        </div>

        {modalAberto && (
          <Modal
            tipo={tipoEdicao}
            onClose={fecharModal}
            usuario={usuario}
            atualizarUsuario={setUsuario}
          />
        )}
      </div>
    </Layout>
  );
};

export default ConfigAll;
