// src/pages/Config.js
import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";
import authService from "../services/authService";
import "../assets/Config.css";

const Config = () => {
  const [email, setEmail] = useState("");
  const [novaSenha, setNovaSenha] = useState("");
  const [usuario, setUsuario] = useState(null);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("usuario"));
    if (storedUser) {
      authService.getUserById(storedUser.id)
        .then((res) => {
          setUsuario(res.data);
          setEmail(res.data.email);
        })
        .catch((err) => {
          console.error("Erro ao carregar dados:", err);
        });
    }
  }, []);

  const handleAlterarSenha = async (e) => {
    e.preventDefault();
    if (!novaSenha || !email) {
      alert("Preencha todos os campos.");
      return;
    }

    try {
      await authService.alterarSenha(usuario.id, email, novaSenha);
      alert("Senha alterada com sucesso!");
      setNovaSenha("");
    } catch (error) {
      alert("Erro ao alterar senha.");
      console.error(error);
    }
  };

  return (
    <Layout>
      <div className="config-container">
        <h2>Alterar Senha</h2>
        <form className="config-form" onSubmit={handleAlterarSenha}>
          <div className="form-group">
            <label>E-mail atual:</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>

          <div className="form-group">
            <label>Nova senha:</label>
            <input type="password" value={novaSenha} onChange={(e) => setNovaSenha(e.target.value)} required />
          </div>

          <button type="submit" className="salvar-btn">Alterar Senha</button>
        </form>
      </div>
    </Layout>
  );
};

export default Config;
