// src/services/AuthService.js
import axios from "axios";

const API_URL = "http://localhost:9087/api/auth"; // ou outra rota que você criar

export const login = async (email, senha) => {
  try {
    const response = await axios.post(`${API_URL}/login`, {
      email,
      senha,
    });
    return response.data;
  } catch (error) {
    throw error.response?.data || { message: "Erro de conexão com o servidor" };
  }
};

export const register = async (dados) => {
  try {
    const response = await axios.post(`${API_URL}/register`, dados);
    return response.data;
  } catch (error) {
    throw error.response?.data || { message: "Erro ao registrar usuário" };
  }
};
