import React, { useState } from 'react';
import { alterarSenha } from '../services/authService';

const Config = () => {
  const [id, setId] = useState('');
  const [email, setEmail] = useState('');
  const [senhaAntiga, setSenhaAntiga] = useState('');
  const [novaSenha, setNovaSenha] = useState('');
  const [mensagem, setMensagem] = useState('');
  const [erro, setErro] = useState('');

  const handleAlterarSenha = async (e) => {
    e.preventDefault();
    setMensagem('');
    setErro('');

    try {
      const response = await alterarSenha(id, email, senhaAntiga, novaSenha);
      setMensagem('Senha alterada com sucesso!');
      setId('');
      setEmail('');
      setSenhaAntiga('');
      setNovaSenha('');
    } catch (err) {
      setErro(err.toString());
    }
  };

  return (
    <div className="p-6 max-w-md mx-auto">
      <h2 className="text-xl font-bold mb-4">Alterar Senha</h2>
      <form onSubmit={handleAlterarSenha} className="space-y-4">
        <input
          type="text"
          placeholder="ID do usuário"
          value={id}
          onChange={(e) => setId(e.target.value)}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="email"
          placeholder="E-mail atual"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="password"
          placeholder="Senha atual"
          value={senhaAntiga}
          onChange={(e) => setSenhaAntiga(e.target.value)}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="password"
          placeholder="Nova senha"
          value={novaSenha}
          onChange={(e) => setNovaSenha(e.target.value)}
          className="w-full p-2 border rounded"
          required
        />
        <button
          type="submit"
          className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
        >
          Alterar Senha
        </button>
      </form>

      {mensagem && <p className="mt-4 text-green-600">{mensagem}</p>}
      {erro && <p className="mt-4 text-red-600">{erro}</p>}

      <hr className="my-6" />

      <button
        className="bg-red-600 text-white py-2 px-4 rounded hover:bg-red-700"
        onClick={() => alert('Funcionalidade de deletar ainda não implementada')}
      >
        Deletar Conta
      </button>
    </div>
  );
};

export default Config;
