import React, { useState } from "react";
import { Link } from "react-router-dom"; // Importa Link para navegação entre páginas
import "../assets/Login.css"; // Certifique-se de importar o arquivo CSS correto

const Registro = () => {
  // Estados para armazenar os valores dos campos do formulário
  const [nome, setNome] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  // Função chamada ao enviar o formulário de registro
  const handleSubmit = (e) => {
    e.preventDefault(); // Impede o recarregamento da página
    if (password !== confirmPassword) {
      alert("As senhas não coincidem!");
      return;
    }
    console.log("Nome:", nome, "E-mail:", email, "Senha:", password);
  };

  return (
    <div className="container"> {/* Ajuste para "container" em vez de "register-container" */}
      <div className="card"> {/* Use "card" como no seu CSS */}
        <h2>Registro</h2>
        {/* Formulário de registro */}
        <form className="form-group" onSubmit={handleSubmit}> {/* Use "form-group" se quiser algo semelhante ao seu CSS */}
          <div className="input-group">
            <label htmlFor="nome">Nome:</label>
            <input
              type="text"
              id="nome"
              className="input" // Use "input" como no seu CSS
              value={nome}
              onChange={(e) => setNome(e.target.value)} // Atualiza o estado ao digitar
              required
            />
          </div>

          <div className="input-group">
            <label htmlFor="email">E-mail:</label>
            <input
              type="email"
              id="email"
              className="input" // Use "input" como no seu CSS
              value={email}
              onChange={(e) => setEmail(e.target.value)} // Atualiza o estado ao digitar
              required
            />
          </div>

          <div className="input-group">
            <label htmlFor="password">Senha:</label>
            <input
              type="password"
              id="password"
              className="input" // Use "input" como no seu CSS
              value={password}
              onChange={(e) => setPassword(e.target.value)} // Atualiza o estado ao digitar
              required
            />
          </div>

          <div className="input-group">
            <label htmlFor="confirmPassword">Confirme sua senha:</label>
            <input
              type="password"
              id="confirmPassword"
              className="input" // Use "input" como no seu CSS
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)} // Atualiza o estado ao digitar
              required
            />
          </div>

          <button className="button" type="submit">Registrar</button> {/* Use a classe "button" do CSS */}
        </form>

        {/* Link para a página de login */}
        <p>
          Já tem uma conta? <Link to="/login">Faça login aqui</Link>
        </p>
      </div>
    </div>
  );
};

export default Registro;
