// src/pages/Login.js
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../assets/Login.css";
import { login } from "../services/authService"; // Importa o serviço de autenticação

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [erro, setErro] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await login(email, password);
      console.log("Login bem-sucedido:", response);
      navigate("/home"); // Redireciona para a home após login bem-sucedido
    } catch (err) {
      console.error("Erro no login:", err);
      setErro(err.message || "Falha ao realizar login");
    }
  };

  return (
    <div className="login-container">
      <div className="login-image-container"></div>
      <h2 className="login-title">Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <label htmlFor="email">E-mail</label>
          <input
            type="email"
            id="email"
            className="input-field"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label htmlFor="password">Senha</label>
          <input
            type="password"
            id="password"
            className="input-field"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="login-button">Entrar</button>
        {erro && <p style={{ color: "red", marginTop: "10px" }}>{erro}</p>}
      </form>
      <p className="signup-link">
        Não tem uma conta? <Link to="/registro">Criar uma conta</Link>
      </p>
    </div>
  );
};

export default Login;
