// src/pages/Home.js
import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import "../assets/Home.css";
import { Chart } from "react-google-charts";
import authService from "../services/authService";

const Home = () => {
  const [renda, setRenda] = useState(0);
  const [gastos, setGastos] = useState([]);
  const [totalGasto, setTotalGasto] = useState(0);
  const [totalInvestimento, setTotalInvestimento] = useState(0);

  useEffect(() => {
    const usuario = JSON.parse(localStorage.getItem("usuario"));
    if (usuario?.id) {
      authService.getUserById(usuario.id)
        .then(res => setRenda(res.data.renda || 0))
        .catch(err => console.error("Erro ao buscar usuário:", err));

      authService.buscarLancamentosPorUsuario(usuario.id)
        .then(res => {
          const todos = res.data;
          setGastos(todos);

          const totalGasto = todos
            .filter(g => g.categoria !== "INVESTIMENTO_E_POUPANCA")
            .reduce((acc, g) => acc + g.valor, 0);

          const totalInvest = todos
            .filter(g => g.categoria === "INVESTIMENTO_E_POUPANCA")
            .reduce((acc, g) => acc + g.valor, 0);

          setTotalGasto(totalGasto);
          setTotalInvestimento(totalInvest);
        })
        .catch(err => console.error("Erro ao buscar lançamentos:", err));
    }
  }, []);

  // Dados para o gráfico (agrupado por categoria)
  const dadosGrafico = [
    ["Categoria", "Valor"],
    ...gastos.reduce((acc, item) => {
      const nome = item.categoria.replace("_E_", " e ").replace("_", " ").toLowerCase();
      const existente = acc.find(i => i[0] === nome);
      if (existente) {
        existente[1] += item.valor;
      } else {
        acc.push([nome, item.valor]);
      }
      return acc;
    }, [])
  ];

  const options = {
    title: "Distribuição de Gastos",
    pieHole: 0.4,
    is3D: false,
  };

  return (
    <Layout>
      <div className="home-container">
        <h1 className="home-header">Relatórios Financeiros</h1>

        <div className="top-cards">
          <div className="stat-card">Rendimento Total<br />{renda.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })}</div>
          <div className="stat-card">Gastos Totais<br />{totalGasto.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })}</div>
          <div className="stat-card">Valor Investido<br />{totalInvestimento.toLocaleString("pt-BR", { style: "currency", currency: "BRL" })}</div>
        </div>

        <div className="dashboard-title">Gráficos</div>

        <div className="dashboard-grid">
          <div className="chart-card">
            <Chart
              chartType="PieChart"
              width="100%"
              height="300px"
              data={dadosGrafico}
              options={options}
            />
          </div>
          <div className="chart-card">Gráfico 2</div>
          <div className="chart-card">Gráfico 3</div>
          <div className="chart-card">Gráfico 4</div>
        </div>
      </div>
    </Layout>
  );
};

export default Home;
