// src/components/EditEmailPopup.js
import React, { useState } from "react";
import authService from "../services/authService";
import Popup from "../assets/Popup.css"

const EditEmailPopup = ({ usuario, onClose }) => {
  const [novoEmail, setNovoEmail] = useState(usuario.email);
  const [mensagem, setMensagem] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    authService.atualizarEmail(usuario.id, novoEmail)
      .then(() => {
        setMensagem("Email atualizado com sucesso.");
        setTimeout(onClose, 1500);
      })
      .catch(() => setMensagem("Erro ao atualizar email."));
  };

  return (
    <div className="popup">
      <h3>Editar Email</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          value={novoEmail}
          onChange={(e) => setNovoEmail(e.target.value)}
          required
        />
        <button type="submit">Salvar</button>
        <button type="button" onClick={onClose}>Cancelar</button>
        {mensagem && <p>{mensagem}</p>}
      </form>
    </div>
  );
};

export default EditEmailPopup;
